import React, { useState } from "react"



const SignUp = (props) => {
    const [username, setUsername] = useState("")
    const [password, setPassword] = useState("")
    const { token, setToken } = props

    //calls to api to register a user
    const SignUpUser = (userObject) => {
        console.log(userObject)
        fetch('http://fitnesstrac-kr.herokuapp.com/api/users/register', {
            method: "POST",
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(userObject)
        }).then(response => response.json())
            .then(result => {
                console.log(result);
                setToken(result.user.token)
                if (result.success === true) {
                    localStorage.setItem("token", token)
                }
            })
            .catch(console.error);
    }

    //calls to api to login user
    const LogInUser = (userObject) => {
        console.log(userObject)
        fetch('http://fitnesstrac-kr.herokuapp.com/api/users/login', {
            method: "POST",
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(userObject)
        }).then(response => response.json())
            .then(result => {
                setToken(result.user.token)
                if (result.success === true) {
                    localStorage.setItem("token", token)
                }
            })
            .catch(console.error);
    }



    const handleSignUp = (event) => {
        event.preventDefault()
        const temp =
        {
            username: username,
            password: password
        }
            ;
        SignUpUser(temp);
        //clears temp object for future inputs
        temp = {};
        setUsername("");
        setPassword("");
    }
    const handleLogIn = (event) => {
        event.preventDefault()
        const temp =
        {
            username: username,
            password: password
        }
            ;
        LogInUser(temp);
        //clears temp object for future inputs
        temp = {};
        setUsername("");
        setPassword("");
    }
    return (
        <>
            <form>
                {/* <input value={user} onChange={handleChange} placeholder="user"></input> */}
                <input placeholder="Username" onChange={(event) => { setUsername(event.target.value) }}></input>
                <input placeholder="Password" onChange={(event) => { setPassword(event.target.value) }}
                ></input>
            </form>
            <button onClick={handleLogIn}>Log-in</button>
            <button onClick={handleSignUp}>
                {" "}
        Don't have an account? Sign up
      </button>
        </>



    )
}
export default SignUp;
