    }, []);

    return (


        <div>routine: {routines.map((route) => {
            <div>
                <h1>{route.name}</h1>
                <h2>Goal: {route.goal}</h2>
                <p>
                    {route.activities.map((ra) => {
                        <div>{ra.name}</div>
                    })}
                </p>
            </div>
        })}
        </div>
    )
}

export default Routines;
